<?php require_once('Connections/sistemai.php'); function cortarTexto($texto, $numMaxCaract){	if (strlen($texto) <  $numMaxCaract){		$textoCortado = $texto;	}else{		$textoCortado = substr($texto, 0, $numMaxCaract);		$ultimoEspacio = strripos($textoCortado, " "); 		if ($ultimoEspacio !== false){			$textoCortadoTmp = substr($textoCortado, 0, $ultimoEspacio);			if (substr($textoCortado, $ultimoEspacio)){				$textoCortadoTmp .= '...';			}			$textoCortado = $textoCortadoTmp;		}elseif (substr($texto, $numMaxCaract)){			$textoCortado .= '...';		}	} 	return $textoCortado;} // VALIDACION DE VERSION DE PHPif (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}mysql_select_db($database_sistemai, $sistemai);$query_config = "SELECT * FROM sis_config";$config = mysql_query($query_config, $sistemai) or die(mysql_error());$row_config = mysql_fetch_assoc($config);$totalRows_config = mysql_num_rows($config);$alias=$_GET['mod'];mysql_select_db($database_sistemai, $sistemai);$query_menu1 = "SELECT *  FROM sis_plantilla_articulos WHERE alias='$alias' AND status=1";$me